def Load(widget):
    if widget.ProviderWidget != None:
        produto = widget.ProviderWidget.Entity

    if produto == None : 
        widget.Hidden = True
        return

    if not produto.ControlaVariacao() :
       widget.Hidden = True

def InitializeRow(entity, row):
    if row.GetCustomCommand("CMD_LOTES") != None:
        row.GetCustomCommand("CMD_LOTES").Enabled = entity.ControlaLotes()
