def OnCONSIDERACANCELADAValueChanged(view, entity):
    from Benner.Tecnologia.Common.Exceptions import CancelledOperationException

    if entity.ExibirAvisoWeb:
        entity.ExibirAvisoWeb = False
        raise CancelledOperationException(entity.MensagemDesconsiderarCanceladas)

