def OnDATAINICIALValueChanged(view, entity):
    from Benner.Tecnologia.Common.Exceptions import CancelledOperationException

    if entity.ExibirAvisoWeb:
        entity.ExibirAvisoWeb = False
        raise CancelledOperationException(entity.MensagemDataFinalMenor)

def OnDATAFINALValueChanged(view, entity):
    from Benner.Tecnologia.Common.Exceptions import CancelledOperationException

    if entity.ExibirAvisoWeb:
        entity.ExibirAvisoWeb = False
        raise CancelledOperationException(entity.MensagemDataFinalMenor)
