def InitializeRow(entity, row):
    if row.EditCommand != None:
        row.EditCommand.Visible = entity.ImplantacaoNaoConfirmada

	if row.DeleteCommand != None:
	    row.DeleteCommand.Visible = entity.ImplantacaoNaoConfirmada

    if row.GetCustomCommand("Confirmar") != None:
        row.GetCustomCommand("Confirmar").Enabled = entity.PodeConfirmarImplantacao
