def InitializeRow(entity, row):
    if row.DeleteCommand != None:
        row.DeleteCommand.Visible = entity.PodeExcluir
	if row.EditCommand != None:
	    row.EditCommand.Visible = entity.VersaoEOrdemEstaoConfirmadas

def Load(widget):
    from Benner.Tecnologia.Common import Entity
    registro = widget.Entity
    if registro == None: return

    if registro.Fields["STATUS"].Value != 2:
        widget.View.Commands["RelatorioContagemManual"].Visible = False
