def Load(widget):
    entidade = widget.Entity
    if(entidade.Fields["CODIGOREFERENCIA"].Instance.Fields["TEMVARIACAO"] == False):
        widget.View.FieldDefinitions["CODIGOREFERENCIAVARIACAO"].Visible = False
        widget.View.FieldDefinitions["CONJUNTOVARIACOES"].Visible = False
