ScriptItemPortable.SourceCode:
def Load(widget):
    import clr
    clr.AddReference("Benner.Erp.Web")
    from Benner.Erp.Web import AuthorizationHelper

    if widget.View.Commands["CMD_NOVOFORNECEDOR"] != None:
        widget.View.Commands["CMD_NOVOFORNECEDOR"].Visible = AuthorizationHelper.HasTask("MANTER_ITENSEMPRESA")

def InitializeRow(entity, row):
    import clr
    clr.AddReference("Benner.Erp.Web")
    from Benner.Erp.Web import AuthorizationHelper

    if row.EditCommand != None:
        row.EditCommand.Visible = AuthorizationHelper.HasTask("MANTER_ITENSEMPRESA")
    if row.DeleteCommand != None:
        row.DeleteCommand.Visible = AuthorizationHelper.HasTask("EXCLUIR_ITENSEMPRESA")
