def InitializeRow(entity, row):

    status = (bool(entity.Fields["STATUS"].Value == 1) and bool(entity.Fields["LIBERADA"]));
    EnableCommand(row.GetCustomCommand("CustomConfirmar"), status);
    EnableCommand(row.GetCustomCommand("CustomRecusar"), status);
    return;

def EnableCommand(command, enabled):
    if command == None: return;
    command.Enabled = enabled;
