ScriptItemPortable.SourceCode:
def Load(widget):

    pai = widget.ProviderWidget;
    if (pai == None): return;
    registro = pai.Entity;
    if (registro == None): return;

    status = int(registro.Fields["RESPOSTAFORNECEDOR"].Instance.Fields["STATUS"].Value);

    widget.View.Commands["New"].Visible = bool(bool(status == 3) or bool(status == 7));

def InitializeRow(entity, row):

    status = int(entity.Fields["RESPOSTAFORNECEDOR"].Instance.Fields["STATUS"].Value);
    delCommand = row.DeleteCommand
    if delCommand != None:
        delCommand.Visible = bool(bool(status == 3) or bool(status == 7));
