ScriptItemPortable.SourceCode:
def InitializeRow(entity, row):
    saldo = entity.Fields["VALOR"] - entity.Fields["VALORESBAIXADOS"];
    row.GetCellByName("CUSTOM_SALDO").Text = saldo.ToString("C2");

    if((entity.Fields["NUMEROBANCO"] == None and entity.Fields["ARQUIVOBLOQUETO"] == None) or saldo <= 0):
        if row.GetCustomCommand("CMD_IMPRIMIR") <> None:
            row.GetCustomCommand("CMD_IMPRIMIR").Enabled = False;
        if row.GetCustomCommand("CMD_ENVIAREMAIL") <> None:            
            row.GetCustomCommand("CMD_ENVIAREMAIL").Enabled = False;
