def InitializeRow(entity, row):
    return;
    emCotacao = (entity.Fields["COTACAO"].Instance.Fields["STATUS"].Value in [2,3]);
    row.EditCommand.Visible = emCotacao;
    row.DeleteCommand.Visible = emCotacao;

def Load(widget):
    pai = widget.ProviderWidget;
    if pai == None: return
    registro = pai.Entity;
    if registro == None: return;
    emCotacao = bool(registro.Fields["STATUS"].Value in [2,3,7]);
    widget.View.Commands["New"].Visible = emCotacao;
    widget.View.Commands["Edit"].Visible = emCotacao;
    widget.View.Commands["Delete"].Visible = emCotacao;
