ScriptItemPortable.SourceCode:
def Load(widget):
    if widget.Entity == None :
        return

    if widget.Entity.HabilitarComandos() :
        widget.View.Commands["New"].Visible = True
        widget.View.Commands["Edit"].Visible = True
        widget.View.Commands["Delete"].Visible = True
        widget.View.Commands["SaveNew"].Visible = True
    else :        
        widget.View.Commands["New"].Visible = False
        widget.View.Commands["Edit"].Visible = False
        widget.View.Commands["Delete"].Visible = False
        widget.View.Commands["SaveNew"].Visible = False
