ScriptItemPortable.SourceCode:
def Load(widget):

    pai = widget.ProviderWidget;
    if pai == None: return
    registro = pai.Entity;
    if registro == None: return;
    emCotacao = bool(registro.Fields["STATUS"].Value in [1]);
    widget.View.Commands["CustomEdicaoRapida"].Visible = emCotacao;

def InitializeRow(entity, row):

    if row.EditCommand == None: return

    row.GetCellByName("NUMEROREQUISICAO").Text = entity.ObterNumeroRequisicaoItem();
    row.EditCommand.Visible = (entity["STATUS"].GetInt64() == 1 and (not entity["EHCANCELADO"].GetBoolean()))
