def InitializeRow(entity, row):
    editavel = (bool) (entity.Fields["REGISTRO"].Instance.Fields["STATUS"].Value in [1, 4]);
    row.EditCommand.Enabled = editavel;
    row.DeleteCommand.Enabled = editavel;

def Load(widget):
    pai = widget.ProviderWidget;
    if pai == None: return
    registro = pai.Entity;
    if registro == None: return;
    editavel = bool(registro.Fields["STATUS"].Value in [1,4]);
    widget.View.Commands["New"].Visible = editavel;
