ScriptItemPortable.SourceCode:
def Load(widget):
    status = widget.Entity.Fields["STATUS"].Value;
    aberto = (bool(status == 3) or bool(status == 7));
    exibirauditoria = (bool(status == 5) or bool(status == 6) or bool(status == 7));

    EnableCommand(widget.Commands["CustomFinalizar"], aberto);
    EnableCommand(widget.Commands["CustomHistorico"], exibirauditoria);

def EnableCommand(command, enabled):
    if command == None: return;
    command.Enabled = enabled;
