ScriptItemPortable.SourceCode:
def Load(widget):
    from Benner.Tecnologia.Common import FieldDefinition

    widget.Entity.BuscarValorTotal()
    widget.Entity.PercentualOnChange()

    if widget.Entity.ExigirProjeto() :
        widget.View.FieldDefinitions["PROJETO"].Required = True
    else :       
        widget.View.FieldDefinitions["PROJETO"].Required = False    

    if widget.Entity["CENTROCUSTO"].GetInt32() > 0 and widget.Entity.HabilitarProjeto() :
        widget.View.FieldDefinitions["PROJETO"].DataAccessLevel = FieldDefinition.AccessLevel.Edit
    else :       
        widget.View.FieldDefinitions["PROJETO"].DataAccessLevel = FieldDefinition.AccessLevel.Read

def OnCENTROCUSTOValueChanged(view, entity):
    from Benner.Tecnologia.Common import FieldDefinition

    if entity["CENTROCUSTO"].GetInt32() > 0 and entity.HabilitarProjeto():
        view.FieldDefinitions["PROJETO"].DataAccessLevel = FieldDefinition.AccessLevel.Edit
    else :       
        view.FieldDefinitions["PROJETO"].DataAccessLevel = FieldDefinition.AccessLevel.Read
