def InitializeRow(entity, row):
    if (entity["TIPODOCUMENTO"].GetString() == "Devolução"):
        row.GetCellByName("DOCUMENTODIGITADO").TextColor = "Red";
        row.GetCellByName("TIPODOCUMENTO").TextColor = "Red";
        row.GetCellByName("DATAEMISSAO").TextColor = "Red";
        row.GetCellByName("DATAENTRADA").TextColor = "Red";
        row.GetCellByName("VALORNOMINAL").TextColor = "Red";
        row.GetCellByName("VALORLIQUIDO").TextColor = "Red";
        row.GetCellByName("VALORESBAIXADOS").TextColor = "Red";
        row.GetCellByName("SALDOPAGAR").TextColor = "Red";
        row.GetCellByName("DATAULTIMALIQ").TextColor = "Red";
        row.GetCellByName("CREDITADO").TextColor = "Red";
        row.GetCellByName("LIBERADO").TextColor = "Red";
        row.GetCellByName("APURADO").TextColor = "Red";
        row.GetCellByName("RECEBIDO").TextColor = "Red";
