ScriptItemPortable.SourceCode:
def Load(widget):
    pai = widget.ProviderWidget;
    if pai == None: return
    registro = pai.Entity;
    if registro == None: return;
    concluido = bool(registro.Fields["STATUS"].Value > 1);
    widget.View.Commands["New"].Visible = not concluido;

def InitializeRow(entity, row):
    concluido = bool(entity.Fields["ORDEMVENDA"].Instance.Fields["STATUS"].Value != 7);
    row.DeleteCommand.Visible = not concluido;
