ScriptItemPortable.SourceCode:
def Load(widget):
    pai = widget.ProviderWidget;
    if pai == None: return
    registro = pai.Entity;
    if registro == None: return;
    concluido = bool(registro.Fields["PEDIDOVENDA"].Instance.Fields["STATUS"].Value > 1);
    widget.View.Commands["CustomAlterar"].Visible = not concluido;
