def InitializeRow(entity, row):
    from Benner.Tecnologia.Common import EntityAssociation
    from Benner.Tecnologia.Common import Handle
    from Benner.Tecnologia.Common import EntityDefinition
    concluido = bool(entity.Fields["PEDIDOVENDA"].Instance.Fields["STATUS"].Value > 1);
    row.EditCommand.Visible = not concluido;
    row.DeleteCommand.Visible = not concluido;
    row.GetCustomCommand("CustomLocalEntrega").Enabled = not concluido;
