ScriptItemPortable.SourceCode:
def Load(widget):
    from Benner.Tecnologia.Common import Entity
    from Benner.Tecnologia.Common import FieldDefinition

    registro = widget.Entity
    if registro == None: return

    stEncerrada = 3

    status = int(registro.Fields["VISITA"].Instance.Fields["STATUS"].Value)
    if (status == stEncerrada):
        if widget.View.Commands["Edit"] != None:
		    widget.View.Commands["Edit"].Visible = False
        if widget.View.Commands["New"] != None:
            widget.View.Commands["New"].Visible = False
        if widget.View.Commands["Save"] != None:
            widget.View.Commands["Save"].Visible = False
        if widget.View.Commands["Delete"] != None:
            widget.View.Commands["Delete"].Visible = False
