ScriptItemPortable.SourceCode:
def Load(widget):
    pai = widget.ProviderWidget;
    if pai == None: return
    registro = pai.Entity;
    if registro == None: return;
    concluido = bool(registro.Fields["PEDIDOVENDA"].Instance.Fields["STATUS"].Value > 1);
    widget.View.Commands["CustomAlterar"].Visible = not concluido;

def OnJPLocalEntregaExecute(command, entity):

    # recuperando o pepdido (e item) atraves do campo lookup no item (ver com Dirceu se pode)
    pedido = entity.Fields["PEDIDOVENDA"].Instance;
    item = entity.Fields["PEDIDOITEM"].Instance;
    abrangencia = "ENTREGA";
    handle = entity["HANDLE"].GetInt32();
    atual = entity["DESTINATARIO"].GetInt32();

    command.OutputDefinition.Parameters["ABRANGENCIA"] = abrangencia;
    command.OutputDefinition.Parameters["ENTREGA"] = handle;
    command.OutputDefinition.Parameters["STATUS"] = pedido["STATUS"].GetInt32();
    command.OutputDefinition.Parameters["CLIENTE"] = pedido["CLIENTE"].GetInt32();
    command.OutputDefinition.Parameters["PEDIDO"] = pedido["HANDLE"].GetInt32();
    command.OutputDefinition.Parameters["ATUAL"] = atual;

