def OnFILTROValueChanged(view, entity):
    from Benner.Tecnologia.Common import FieldDefinition;

    view.FieldDefinitions["FATURAMENTO"].DataAccessLevel = FieldDefinition.AccessLevel.Edit;
    view.FieldDefinitions["FATURAMENTOATE"].DataAccessLevel = FieldDefinition.AccessLevel.Edit;
    view.FieldDefinitions["VENCIMENTOTITULOCLIENTE"].DataAccessLevel = FieldDefinition.AccessLevel.Edit;
    view.FieldDefinitions["VENCIMENTOTITULOCLIENTEATE"].DataAccessLevel = FieldDefinition.AccessLevel.Edit;
    view.FieldDefinitions["RECEBIMENTOCLIENTE"].DataAccessLevel = FieldDefinition.AccessLevel.Edit;
    view.FieldDefinitions["RECEBIMENTOCLIENTEATE"].DataAccessLevel = FieldDefinition.AccessLevel.Edit;
    view.FieldDefinitions["APURACAOCOMISSAO"].DataAccessLevel = FieldDefinition.AccessLevel.Edit;
    view.FieldDefinitions["APURACAOCOMISSAOATE"].DataAccessLevel = FieldDefinition.AccessLevel.Edit;
    view.FieldDefinitions["PAGAMENTOCOMISSAO"].DataAccessLevel = FieldDefinition.AccessLevel.Edit;
    view.FieldDefinitions["PAGAMENTOCOMISSAOATE"].DataAccessLevel = FieldDefinition.AccessLevel.Edit;
    view.FieldDefinitions["CLIENTES"].DataAccessLevel = FieldDefinition.AccessLevel.Edit;

    if int(entity.Fields["FILTRO"].Value) == 1:
        view.FieldDefinitions["FATURAMENTO"].DataAccessLevel = FieldDefinition.AccessLevel.Read;
        view.FieldDefinitions["FATURAMENTOATE"].DataAccessLevel = FieldDefinition.AccessLevel.Read;
        view.FieldDefinitions["VENCIMENTOTITULOCLIENTE"].DataAccessLevel = FieldDefinition.AccessLevel.Read;
        view.FieldDefinitions["VENCIMENTOTITULOCLIENTEATE"].DataAccessLevel = FieldDefinition.AccessLevel.Read;
        view.FieldDefinitions["RECEBIMENTOCLIENTE"].DataAccessLevel = FieldDefinition.AccessLevel.Read;
        view.FieldDefinitions["RECEBIMENTOCLIENTEATE"].DataAccessLevel = FieldDefinition.AccessLevel.Read;
        view.FieldDefinitions["APURACAOCOMISSAO"].DataAccessLevel = FieldDefinition.AccessLevel.Read;
        view.FieldDefinitions["APURACAOCOMISSAOATE"].DataAccessLevel = FieldDefinition.AccessLevel.Read;
        view.FieldDefinitions["PAGAMENTOCOMISSAO"].DataAccessLevel = FieldDefinition.AccessLevel.Read;
        view.FieldDefinitions["PAGAMENTOCOMISSAOATE"].DataAccessLevel = FieldDefinition.AccessLevel.Read;

    if (int(entity.Fields["FILTRO"].Value) == 2) or (int(entity.Fields["FILTRO"].Value) == 3):
        view.FieldDefinitions["APURACAOCOMISSAO"].DataAccessLevel = FieldDefinition.AccessLevel.Read;
        view.FieldDefinitions["APURACAOCOMISSAOATE"].DataAccessLevel = FieldDefinition.AccessLevel.Read;
        view.FieldDefinitions["PAGAMENTOCOMISSAO"].DataAccessLevel = FieldDefinition.AccessLevel.Read;
        view.FieldDefinitions["PAGAMENTOCOMISSAOATE"].DataAccessLevel = FieldDefinition.AccessLevel.Read;
