def InitializeRow(entity, row):
    if entity["CALCULOGERADO"].GetBoolean():
        if row.EditCommand != None:
            row.EditCommand.Visible = False;
        if row.DeleteCommand != None:
            row.DeleteCommand.Visible = False;
    if entity["STATUSCALCULO"].GetInt64() == 2 or entity["STATUSCALCULO"].GetInt64() == 3 or entity["STATUSCALCULO"].GetInt64() == 5 or entity["STATUSCALCULO"].GetInt64() == 4:
        if row.EditCommand != None:
            row.EditCommand.Visible = False;
        if row.DeleteCommand != None:
            row.DeleteCommand.Visible = False;
