ScriptItemPortable.SourceCode:
def Load(widget):
    EntidadeOR = widget.Entity
    if EntidadeOR == None:
        return
    Liberada = EntidadeOR.Fields["LIBERADA"]
    if widget.View.Commands["Edit"] != None:
        widget.View.Commands["Edit"].Visible = False
    if widget.View.Commands["Delete"] != None:
        widget.View.Commands["Delete"].Visible = False

    if widget.View.Commands["CMD_GERENCIAR_APONTAMENTOS"] != None:
        widget.View.Commands["CMD_GERENCIAR_APONTAMENTOS"].Visible = False

    if (EntidadeOR.Fields["STATUS"].Value == 1):
        if widget.View.Commands["Edit"] != None:
            widget.View.Commands["Edit"].Visible = True
        if widget.View.Commands["Delete"] != None:
            widget.View.Commands["Delete"].Visible = True

    if (EntidadeOR.Fields["STATUS"].Value in (2,3)):
            if widget.View.Commands["CMD_GERENCIAR_APONTAMENTOS"] != None:
                widget.View.Commands["CMD_GERENCIAR_APONTAMENTOS"].Visible = True

    if(Liberada):
        if widget.View.Commands["Edit"] != None:
            widget.View.Commands["Edit"].Visible = False
        if widget.View.Commands["Delete"] != None:
            widget.View.Commands["Delete"].Visible = False
