ScriptItemPortable.SourceCode:
def Load(widget):
    from Benner.Tecnologia.Business import Query
    mestre = widget.ProviderWidget
    if mestre != None:
        entidadeMestre = mestre.Entity
        if entidadeMestre != None :
            sqlProduto = Query("SELECT LOTE,TEMVARIACAO FROM PD_PRODUTOS WHERE HANDLE = "+str(entidadeMestre["PRODUTO"].GetInt64())+"");
            sqlApontamento = Query("SELECT STATUS FROM PR_ORDEMRETRABALHOAPONTAMENTO WHERE HANDLE IN (SELECT APONTAMENTO FROM PR_ORDEMRETRABALHOAPONTITENS WHERE HANDLE =  "+str(entidadeMestre["HANDLE"].GetInt64())+")");
            resultadoProduto = sqlProduto.Execute()
            resultadoApontamento = sqlApontamento.Execute()

            if widget.View.Commands["Edit"] != None:
                widget.View.Commands["Edit"].Visible = False
            if widget.View.Commands["Delete"] != None:
                widget.View.Commands["Delete"].Visible = False
            if widget.View.Commands["New"] != None:
                widget.View.Commands["New"].Visible = False
            if(resultadoApontamento.Count > 0):
                if(resultadoApontamento[0]["STATUS"].GetInt64() == 1):
                    if(resultadoProduto != None):
                        if(resultadoProduto[0]["LOTE"].GetBoolean()):
                            if widget.View.Commands["Edit"] != None:
                                widget.View.Commands["Edit"].Visible = True
                            if widget.View.Commands["Delete"] != None:
                                widget.View.Commands["Delete"].Visible = True
                            if widget.View.Commands["New"] != None:
                                widget.View.Commands["New"].Visible = True
