def InitializeRow(entity, row):
    row.GetCellByName("DATAENTRADA").Text = entity.Fields["DATAENTRADA"].ToString("dd/MM/yyyy");
    row.GetCellByName("DATAVENCIMENTO").Text = entity.Fields["DATAVENCIMENTO"].ToString("dd/MM/yyyy");
    row.GetCellByName("DATAEMISSAO").Text = entity.Fields["DATAEMISSAO"].ToString("dd/MM/yyyy");
    row.GetCellByName("VCTOPRORROGADO").Text = entity.Fields["VCTOPRORROGADO"].ToString("dd/MM/yyyy");
