def OnCMD_ENVIAREVENTOR2098Execute(command, entity):
    from System import String

    if entity == None:
        return

    command.RequestConfirmationBeforeExecute("Essa ação enviará o evento R-2098 para reabrir a competência "+ entity.Fields["COMPETENCIA"].ToString("MM/yyyy") +" da EFD REINF da empresa. Tem certeza que desejar reabrir esse período?")
