def Load(widget):
    from Benner.Tecnologia.Common import ListItem
    form = widget.ProviderWidget
    entity = form.Entity
    item = entity.Fields["REGISTRO"]
    if entity != None:
        if item != ListItem(2,"R-2010 - Retenção contribuição previdenciária - Serviços tomados"):
            widget.Hidden = True
