def Load(widget):
    from Benner.Tecnologia.Common import ListItem
    form = widget.ProviderWidget
    entity = form.Entity
    item = entity.Fields["REGISTRO"]
    if entity != None:
        if item != ListItem(3,"R-2020 - Retenção contribuição previdenciária - Serviços prestados"):
            widget.Hidden = True
