def Load(widget):
    from Benner.Tecnologia.Common import ListItem
    form = widget.ProviderWidget
    entity = form.Entity
    item = entity.Fields["REGISTRO"]
    if entity != None:
        if item != ListItem(4,"R-2030 - Recursos recebidos por associação desportiva"):
            widget.Hidden = True
