def Load(widget):
    from Benner.Tecnologia.Common import ListItem
    form = widget.ProviderWidget
    entity = form.Entity
    item = entity.Fields["REGISTRO"]
    if entity != None:
        if item != ListItem(6,"R-2050 - Comercialização da produção por produtor rural PJ/Agroindústria"):
            widget.Hidden = True
