def Load(widget):
    from Benner.Tecnologia.Common import ListItem
    form = widget.ProviderWidget
    entity = form.Entity
    item = entity.Fields["REGISTRO"]
    if entity != None:
        if item != ListItem(10,"R-2055 - Aquisição de Produtor Rural"):
            widget.Hidden = True
