def Load(widget):
    import clr
    clr.AddReference("Benner.Corporativo.Definicoes")

    from Benner.Corporativo.Definicoes.SPED.REINF import REINFR2055DetalheAquisicaoDoc

    widget.Hidden = widget.ProviderWidget.Entity == None or widget.ProviderWidget.Entity.Handle.IsInvalid() or  not REINFR2055DetalheAquisicaoDoc.DetalhamentoPossuiDocumento(widget.ProviderWidget.Entity.Handle);
