def Load(widget):
    from Benner.Tecnologia.Common import ListItem
    form = widget.ProviderWidget
    entity = form.Entity
    item = entity.Fields["REGISTRO"]
    if entity != None:
        if item != ListItem(7,"R-2060 - Contribuição previdenciária sobre a receita bruta - CPRB"):
            widget.Hidden = True
