def InitializeRow(entity, row):

    row.GetCellByName("VLRDEDSUSP").Text = "0,00";


    if entity.Fields["VLRDEDSUSP"] != None: row.GetCellByName("VLRDEDSUSP").Text = str(str(str("{:,.2f}".format(entity.Fields["VLRDEDSUSP"])).replace(".","_")).replace(",",".")).replace("_",",").replace("-","");
