def InitializeRow(entity, row):

    row.GetCellByName("VLRSAUDE").Text = "0,00";


    if entity.Fields["VLRSAUDE"] != None: row.GetCellByName("VLRSAUDE").Text = str(str(str("{:,.2f}".format(entity.Fields["VLRSAUDE"])).replace(".","_")).replace(",",".")).replace("_",",").replace("-","");
