def InitializeRow(entity, row):

    row.GetCellByName("VLRRENDBRUTO").Text = "0,00";




    if entity.Fields["VLRRENDBRUTO"] != None: row.GetCellByName("VLRRENDBRUTO").Text = str(str(str("{:,.2f}".format(entity.Fields["VLRRENDBRUTO"])).replace(".","_")).replace(",",".")).replace("_",",").replace("-","");
