def InitializeRow(entity, row):

    row.GetCellByName("VLRDEPEN").Text = "0,00";

    #Formatação dos valores
    if entity.Fields["VLRDEPEN"] != None: row.GetCellByName("VLRDEPEN").Text = str(str(str("{:,.2f}".format(entity.Fields["VLRDEPEN"])).replace(".","_")).replace(",",".")).replace("_",",").replace("-","");
