def InitializeRow(entity, row):

    row.GetCellByName("VLRDEPENSUSP").Text = "0,00";


    if entity.Fields["VLRDEPENSUSP"] != None: row.GetCellByName("VLRDEPENSUSP").Text = str(str(str("{:,.2f}".format(entity.Fields["VLRDEPENSUSP"])).replace(".","_")).replace(",",".")).replace("_",",").replace("-","");
