def InitializeRow(entity, row):

    row.GetCellByName("VLRISENTO").Text = "0,00";

    #Formatação dos valores
    if entity.Fields["VLRISENTO"] != None: row.GetCellByName("VLRISENTO").Text = str(str(str("{:,.2f}".format(entity.Fields["VLRISENTO"])).replace(".","_")).replace(",",".")).replace("_",",").replace("-","");
