def InitializeRow(entity, row):

    if entity == None: return

    if entity.Fields["VLRBRUTO"] != None: row.GetCellByName("VLRBRUTO").Text = str(str(str("{:,.2f}".format(entity.Fields["VLRBRUTO"])).replace(".","_")).replace(",",".")).replace("_",",").replace("-","")
