def Load(widget):
    from Benner.Tecnologia.Common import ListItem
    form = widget.ProviderWidget
    entity = form.Entity
    item = entity.Fields["REGISTRO"]
    if entity != None:
        if item != ListItem(13,"R-4020 - Pagamentos/Créditos a Beneficiário pessoa Jurídica"):
            widget.Hidden = True
