def InitializeRow(entity, row):
    if(entity.Fields["VALOR"] != None and row.GetCellByName("VALOR") != None):
        row.GetCellByName("VALOR").Text = entity.Fields["VALOR"].ToString("C2")
    if(entity.Fields["VALORLIQUIDO"] != None and row.GetCellByName("VALORLIQUIDO") != None):
        row.GetCellByName("VALORLIQUIDO").Text = entity.Fields["VALORLIQUIDO"].ToString("C2")
    if(entity.Fields["TITULOS"] != None and row.GetCellByName("TITULOS") != None):
        row.GetCellByName("TITULOS").Text = entity.Fields["TITULOS"].ToString()
