def OnCMD_PreverExecute(command, entity):
    from Benner.Tecnologia.Common import ReportUtils
    ReportUtils.ConfigureReportCommand(command, entity)

def InitializeRow(entity, row):
    if row.GetCustomCommand("CMD_EditarLayout") != None:
		row.GetCustomCommand("CMD_EditarLayout").Enabled = entity["TIPO"].GetInt32() == 2
