def InitializeRow(entity, row):
    faturado = entity.Fields["DOCUMENTOSUSPENSO"]
    cmd = row.GetCustomCommand("CMD_LIBERAR");
    cmd2 = row.GetCustomCommand("CMD_SUSPENDER");

    if (cmd != None):
        cmd.Enabled = faturado
        cmd2.Enabled = not faturado

    if faturado :
        row.GetCellByName("SITUACAO").Text = "<div class='label label-sm label-danger'>"+ "Suspensa" +"</div>"
    else:
        row.GetCellByName("SITUACAO").Text = "<div class='label label-sm label-info'>" + "A faturar" + "</div>"

    if entity.Fields["LIBERADO"] :
        row.GetCellByName("SITUACAO").Text = "<div class='label label-sm label-success'>"+ "Faturamento encerrado" +"</div>"

    if entity.Fields["GARANTIA"] :
        row.GetCellByName("SITUACAO").Text = "<div class='label label-sm label-warning'>"+ "Em garantia" +"</div>"   

    if entity.Fields["GARANTIA"] :
        row.GetCellByName("SITUACAO").Text = "<div class='label label-sm label-warning'>"+ "Concedido desconto total" +"</div>"

def Load(widget):
    parentNode = widget.Breadcrumb.Parent
    if parentNode != None:
        if parentNode.Entity.PhysicalTableName == "VT_CM_CONTRATOITEMPERIODOS":
            widget.View.Commands["CMD_CONFIRMAR"].Visible = False
        else:
            widget.View.Commands["CMD_CONFIRMARADITIVO"].Visible = False
