def Load(widget):
    form = widget.ProviderWidget
    entity = form.Entity

    if (entity == None) : return

    if ( (str(widget.ID) == "CONFIGURAESAPURAOPRESUMIDOSC") and  ( entity["ICMS"].GetDBValue() == "S"  ) ):
        widget.Hidden = False
    else:
        widget.Hidden = True
