ScriptItemPortable.SourceCode:
def InitializeRow(entity, row):
    if entity == None : return

    if row.GetCustomCommand("CMD_EMITIR") != None:
        row.GetCustomCommand("CMD_EMITIR").Enabled = int(entity.Fields["STATUS"].Value) == 1
    if row.GetCustomCommand("CMD_CONSULTAR") != None:
        row.GetCustomCommand("CMD_CONSULTAR").Enabled = int(entity.Fields["STATUS"].Value) == 2
    #row.GetCustomCommand("CMD_CONSULTARENCERRAMENTO").Enabled = int(entity.Fields["STATUS"].Value) == 4;
    if row.GetCustomCommand("CMD_ENCERRAR") != None:
        row.GetCustomCommand("CMD_ENCERRAR").Enabled = int(entity.Fields["STATUS"].Value) == 3
    if row.GetCustomCommand("CMD_DAMDFE") != None:
        row.GetCustomCommand("CMD_DAMDFE").Enabled = int(entity.Fields["STATUS"].Value) >= 3
