ScriptItemPortable.SourceCode:
def InitializeRow(entity, row):
    if entity == None: return

    #Zerando valores
    row.GetCellByName("VALORAJUSTEBC").Text = "0,00";

    #Formatação dos valores
    if entity.Fields["VALORAJUSTEBC"] != None: row.GetCellByName("VALORAJUSTEBC").Text = str(str(str("{:,.2f}".format(entity.Fields["VALORAJUSTEBC"])).replace(".","_")).replace(",",".")).replace("_",",").replace("-","");
