ScriptItemPortable.SourceCode:
def InitializeRow(entity, row):
    if entity == None: return

    #Zerando valores
    row.GetCellByName("VALORAJUSTE").Text = "0,00";

    #Formatação dos valores
    if entity.Fields["VALORAJUSTE"] != None: row.GetCellByName("VALORAJUSTE").Text = str(str(str("{:,.2f}".format(entity.Fields["VALORAJUSTE"])).replace(".","_")).replace(",",".")).replace("_",",").replace("-","");
